INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (1, 'Yamaha 250CC XYZ ', 'Nice Bike, good condition, low mileage.', 45, 'MTX', '/r2-images/bb-yam-1.jpg', '["/r2-images/b1.jpg", "/r2-images/b2.jpg", "/r2-images/b3.jpg", "/r2-images/b4.jpg"]', 'nice bike, good condition, low mileage. Recently serviced with new tires and brake pads. Perfect for city commuting or weekend rides. Contact for more details or to arrange a viewing.');
INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (2, 'Yamaha 125CC ABC', 'Banging bike, great condition, very low mileage.', 30, 'MTX', '/r2-images/bb-yam-2.jpg', '["/r2-images/ben-cyr-1.jpg", "/r2-images/ben-cyr-2.jpg", "/r2-images/ben-cyr-3.jpg"]', 'First to see will buy! This bike is in excellent condition with very low mileage. It has been well maintained and recently had a full service. Ideal for new riders or as a reliable second bike. Don''t miss out on this great deal!');
INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (3, 'Honda Firenza 250CC', 'Limited edition bike, great for the collector.', 25, 'ROAD', '/r2-images/trusselbb-yam-3.jpg', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', ' never been on the road, this limited edition Honda Firenza 250CC is a must-have for collectors. With only a few units ever made, it features unique styling and top-notch performance. Comes with original documentation and accessories.');
INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (4, 'KTM 300CC Adventure', 'No better bike for the trails, ready to ride.', 35, 'MTX', '/r2-images/bb-yam-4.jpg', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'Captured at dawn in Richmond Park, this macro photograph shows the intricate beauty of morning dew on a spider''s web. Shot with a 100mm macro lens and printed on premium photographic paper. Comes matted and framed.');
INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (5, 'Abstract Emotions', 'Mixed media painting exploring color and form. Bold, contemporary piece.', 60, 'painting', '/r2-images/r2-g1.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'This bold abstract piece uses mixed media including acrylics, ink, and collage elements to explore themes of emotion and human connection. The vibrant colors and dynamic composition make it a striking focal point for any room.');
INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (6, 'Portrait Study', 'Pencil portrait demonstrating classical drawing techniques.', 40, 'drawing', '/r2-images/r2-g1.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'A detailed pencil portrait study showcasing traditional drawing techniques. This piece demonstrates careful observation of light, shadow, and form. Completed on high-quality drawing paper using graphite pencils ranging from 2H to 8B.');
INSERT INTO bens_bikes_products (id, title, description, price, category, image, images, longDescription)
VALUES (7, 'Portrait Study', 'Pencil portrait demonstrating classical drawing techniques.', 40, 'drawing', '/r2-images/r2-jwh.png', '["/r2-images/r2-g1.png", "/r2-images/r2-g2.png", "/r2-images/r2-g4.png"]', 'A detailed pencil portrait study showcasing traditional drawing techniques. This piece demonstrates careful observation of light, shadow, and form. Completed on high-quality drawing paper using graphite pencils ranging from 2H to 8B.');
